descriptor.templateCss:
.tb-rpc-button {
    width: 100%;
    height: 100%;
}

.tb-rpc-button .title-container {
    font-weight: 500;
    white-space: nowrap;
    margin: 10px 0;
}

.tb-rpc-button .button-container div{
    min-width: 80%
}

.tb-rpc-button .button-container .md-button{
    width: 100%;
    margin: 0;
}

.tb-rpc-button .error-container {
    position: absolute;
    top: 2%;
    right: 0;
    left: 0;
    z-index: 4;
    height: 14px;
}

.tb-rpc-button .error-container .button-error {
    color: #ff3315;
    white-space: nowrap;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.buttonLabel = self.ctx.settings.buttonText;
    self.ctx.$scope.showTitle = self.ctx.settings.title &&
        self.ctx.settings.title.length ? true : false;
    self.ctx.$scope.title = self.ctx.settings.title;
    self.ctx.$scope.styleButton = self.ctx.settings.styleButton;
    let entityAttributeType = self.ctx.settings.entityAttributeType;
    let entityParameters = JSON.parse(self.ctx.settings.entityParameters);
    if (self.ctx.settings.styleButton.isPrimary ===
        false) {
        self.ctx.$scope.customStyle = {
            'background-color': self.ctx.$scope.styleButton
                .bgColor,
            'color': self.ctx.$scope.styleButton.textColor
        };
    }

let $scope = self.ctx.$scope;
$scope.userId = $scope.$injector.get("userService").getCurrentUser().customerId;
console.log($scope.userId, 'test')
$scope.token = $scope.$injector.get("userService").getJwtToken();
$scope.authority = $scope.$injector.get("userService").getCurrentUser().authority;
if($scope.authority === 'TENANT_ADMIN'){
$scope.userId = $scope.$injector.get("userService").getCurrentUser().userId;
}

$scope = self.ctx.$scope;
console.log('$scope', $scope.$injector.get("userService").getCurrentUser());
console.log(`http://localhost:5173/${$scope.userId}/?token=${$scope.token}?authority=${$scope.authority}`)

self.ctx.$scope.sendUpdate = function() {
    document.location.href = `http://localhost:3000/${$scope.userId}/?token=${$scope.token}&authority=${$scope.authority}`

}



};

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },
            "buttonText": {
                "title": "Button label",
                "type": "string",
                "default": "Update device attribute"
            },
            "entityAttributeType": {
                "title": "Device attribute scope",
                "type": "string",
                "default": "SERVER_SCOPE"
            },
            "entityParameters": {
                "title": "Device attribute parameters",
                "type": "string",
                "default": "{}"
            },
            "styleButton": {
                "type": "object",
                "title": "Button Style",
                "properties": {
                    "isRaised": {
                        "type": "boolean",
                        "title": "Raised",
                        "default": true
                    },
                    "isPrimary": {
                        "type": "boolean",
                        "title": "Primary color",
                        "default": false
                    },
                    "bgColor": {
                        "type": "string",
                        "title": "Button background color",
                        "default": null
                    },
                    "textColor": {
                        "type": "string",
                        "title": "Button text color",
                        "default": null
                    }
                }
            },
            "required": []
        }
    },
    "form": [
        "title",
        "buttonText",
        {
            "key": "entityAttributeType",
            "type": "rc-select",
            "multiple": false,
            "items": [{
                "value": "SERVER_SCOPE",
                "label": "Server attribute"
            }, {
                "value": "SHARED_SCOPE",
                "label": "Shared attribute"
            }]
        }, {
            "key": "entityParameters",
            "type": "json"
        },
        {
            "key": "styleButton",
            "items": [
                "styleButton.isRaised",
                "styleButton.isPrimary",
                {
                    "key": "styleButton.bgColor",
                    "type": "color"
                },
                {
                    "key": "styleButton.textColor",
                    "type": "color"
                }
            ]
        }
    ]

}
